'                 write(*,''(*(a))'') ''*dem_backspace* ERROR:'',mssge', &
'         endif', &
'      enddo', &
'      read(10,''(a)'')line', &
'      write(*,*)''back at a previous record !''', &
'      write(*,''(1x,a)'')line', &
