case('51','close')

textblock=[character(len=256) :: &
'', &
'NAME', &
'   CLOSE(7f) - [FORTRAN:IO] terminate the connection of a specified unit', &
'   to an external file.', &
'   ', &
'SYNOPSIS', &
'   CLOSE ( [UNIT= ] file-unit-number,', &
