!!       write(*,*)'========================='
!!       do i30=1,9
!!          call dp_accdig(1.2345678d0,vals(i30),8.0,acurcy1,ind1)
!!          call dp_accdig(vals(i30),1.2345678d0,8.0,acurcy2,ind2)
!!          write(*,*)i30,vals(i30),acurcy1,acurcy2,ind1,ind2
